import { useState, useEffect } from 'react'

const useTransition = (valueFrom, valueTo, seconds = 1) => {
    const [transitionValue, setTransitionValue] = useState(valueFrom, valueTo);

    useEffect(() => {
     const interval = setInterval(() => {
            setTransitionValue(transitionValue+getChange(valueFrom, valueTo, within))
        }, 500);

    return () => clearInterval(interval);

    }, []);

    return transitionValue;
}

const getChange = (from, to, seconds) => {
    return (from-to)/seconds;
}

export {
    useTransition
}
