import React from 'react';
import './App.css';
import {useTransition} from './lib/useTransition.js'
function App() {
  const color = useTransition("#dd66ff", "#ffcc99", 0.2);
  const fontSize = useTransition(20,100,0.5, () => console.log("done"));
  const text = useTransition("", "Hello World!",0.2);
  const opacity = useTransition(0, 1, 0.5);
  const movement = useTransition(0,90, 2)
  return (
    <div className="App">
      <div style = {{position:'absolute',fontSize, color, opacity}}>{text}</div>
      <div style={{position:'absolute', height:'20px', width:'20px', borderRadius:'10px', backgroundColor:'green', top:movement+'vh', left:movement+'vw'}}>
        &nbsp;
      </div>
    </div>
  );
}

export default App;
