import { useState, useEffect } from 'react'

const useTransition = (from, to, seconds = 1) => {
    const [valueFrom, valueTo] = decodeFromTo(from, to);
    const [transitionValue, setTransitionValue] = useState(valueFrom, valueTo);

    useEffect(() => {
        if (valueTo != null) {
            const velocity = getVelocityInMillis(valueFrom, valueTo, seconds);
            const interval = setInterval(() => {
                setTransitionValue(transitionValue + velocity)
            }, velocity * 1000);

            if (transitionValue === valueTo) {
                clearInterval(interval);
            }

            return () => clearInterval(interval);
        }

    }, [seconds, transitionValue, valueFrom, valueTo]);

    if (valueTo == null)
        return from;


    return transitionValue;
}


const decodeFromTo = (from, to) => {
    if (typeof (from) !== typeof (to)) {
        return [null, null];
    }

    switch (typeof (from)) {
        case "number":
            return [from, to];
        default:
            return [null, null];
    }
}

const getVelocityInMillis = (from, to, seconds) => {
    if (to !== from) {
        return (to - from) / seconds;
    }
    return from;
}

export {
    useTransition
}
