import React from 'react';
import './App.css';
import {useTransition} from './lib/useTransition.js'
function App() {
  const color = useTransition("#dd66ff", "#ffcc99", 0.2);
  const fontSize = useTransition(0,100,0.5);
  const text = useTransition("", "Hello World!",0.2);
  const opacity = useTransition(0, 1, 0.5);
  const movement = useTransition(0,90, 0.5)
  const fasterMovement = useTransition(0,90, 0.2)
  return (
    <div className="App">
      <div style = {{fontSize, color, opacity}}>{text}</div>
      <div style={{position:'absolute', height:'10px', backgroundColor:'orange', top:movement+'vh', left:fasterMovement+'vw'}}>
        a
      </div>
    </div>
  );
}

export default App;
