import { useState, useEffect } from 'react'

const IS_NUMBER = "IS_NUMBER";
const IS_UNKNOWN = "IS_UNKNOWN";
const IS_INCOMPARABLE = "IS_INCOMPARABLE"
const IS_STRING = "IS_STRING";
const IS_COLOR = "IS_COLOR";

const useTransition = (from, to, seconds = 1) => {
    //const [valueFrom, valueTo] = decodeFromTo(from, to);
    //factory checks type (number, percentage, color or string)
    return transitionFactory(from, to, seconds);


}

const transitionFactory = (from, to, seconds) => {
    const inputType = transitionType(from, to);

    switch (inputType) {
        case IS_NUMBER:
            return IncrementNumber(from, to, seconds);
        case IS_STRING:
            return IncrementString(from, to, seconds);
        case IS_COLOR:
            return "color";
        case IS_UNKNOWN:
        case IS_INCOMPARABLE:
        default:
            return from;
    }  
}

const IncrementNumber = (from, to, seconds) => {
    return useIncrement(from, to, seconds);
}

const IncrementString = (from, to, seconds) => {
    const fromLength = from.length;
    const toLength = to.length;

    return to.substring(from.Length, Math.round(useIncrement(fromLength, toLength, seconds)));

}

const transitionType = (from, to, seconds) => {
    if (typeof (from) !== typeof (to)) {
        return IS_INCOMPARABLE;
    }

    switch (typeof (from)) {
        case "number":
            return IS_NUMBER;
        case "string":
            if (areColors(from, to)) {
                return IS_COLOR;
            }
            return IS_STRING;
        default:
            return IS_UNKNOWN;
    }
}

const areColors = (from, to) => {
    return !from.search(/^#([0-9A-F]{3}){1,2}$/i) && !to.search(/^#([0-9A-F]{3}){1,2}$/i);
}

const useIncrement = (from, to, seconds) => {
    const [transitionValue, setTransitionValue] = useState(from, to);

    useEffect(() => {
        if (to != null) {
            const velocity = getVelocityInMillis(from, to, seconds);
            const interval = setInterval(() => {
                setTransitionValue(transitionValue + velocity)
            }, velocity * 1000);

            if (transitionValue >= to) {
                setTransitionValue(to);
                clearInterval(interval);
            }

            return () => clearInterval(interval);
        }

    }, [seconds, transitionValue, from, to]);


    return transitionValue;
}

const getVelocityInMillis = (from, to, seconds) => {
    if (to !== from) {
        return (to - from) / seconds;
    }
    return from;
}

export {
    useTransition
}
