import { useState, useEffect } from 'react'

const useTransition = (from, to, seconds = 1) => {
    //const [valueFrom, valueTo] = decodeFromTo(from, to);
    //factory checks type (number, percentage, color or string)
    const value = useIncrement(from, to, seconds);
    return value;

}

const useIncrement = (from, to, seconds) => {
    const [transitionValue, setTransitionValue] = useState(from, to);

    useEffect(() => {
        if (to != null) {
            const velocity = getVelocityInMillis(from, to, seconds);
            const interval = setInterval(() => {
                setTransitionValue(transitionValue + velocity)
            }, velocity * 1000);

            if (transitionValue >= to) {
                setTransitionValue(to);
                clearInterval(interval);
            }

            return () => clearInterval(interval);
        }

    }, [seconds, transitionValue, from, to]);


    return transitionValue;
}

const decodeFromTo = (from, to) => {
    if (typeof (from) !== typeof (to)) {
        return [null, null];
    }

    switch (typeof (from)) {
        case "number":
            return [from, to];
        case "string":
            return [0, 10];
        default:
            return [null, null];
    }
}

const getVelocityInMillis = (from, to, seconds) => {
    if (to !== from) {
        return (to - from) / seconds;
    }
    return from;
}

export {
    useTransition
}
