import { useState, useEffect } from 'react'

const IS_NUMBER = "IS_NUMBER";
const IS_UNKNOWN = "IS_UNKNOWN";
const IS_INCOMPARABLE = "IS_INCOMPARABLE"
const IS_STRING = "IS_STRING";

const useTransition = (from, to, seconds = 1) => {
    //const [valueFrom, valueTo] = decodeFromTo(from, to);
    //factory checks type (number, percentage, color or string)

    const inputType = transitionTypeFactory(from, to);

    switch (inputType) {
        case IS_NUMBER:
            return Increment(from, to, seconds);
        case IS_UNKNOWN:
        case IS_INCOMPARABLE:
        default:
            return from;
    }

}


const transitionTypeFactory = (from, to, seconds) => {
    if (typeof (from) !== typeof (to)) {
        return IS_INCOMPARABLE;
    }

    switch (typeof (from)) {
        case "number":
            return IS_NUMBER;
        case "string":
            return IS_STRING;
        default:
            return IS_UNKNOWN;
    }
}

const Increment = (from, to, seconds) => {
    const [transitionValue, setTransitionValue] = useState(from, to);

    useEffect(() => {
        if (to != null) {
            const velocity = getVelocityInMillis(from, to, seconds);
            const interval = setInterval(() => {
                setTransitionValue(transitionValue + velocity)
            }, velocity * 1000);

            if (transitionValue >= to) {
                setTransitionValue(to);
                clearInterval(interval);
            }

            return () => clearInterval(interval);
        }

    }, [seconds, transitionValue, from, to]);


    return transitionValue;
}

const getVelocityInMillis = (from, to, seconds) => {
    if (to !== from) {
        return (to - from) / seconds;
    }
    return from;
}

export {
    useTransition
}
